import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Carousel from './components/Carousel';
import './styles/main.css';
import './styles/theme.css';
import './styles/carousel.css';

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Check time for automatic dark mode
  useEffect(() => {
    const hour = new Date().getHours();
    const autoDarkMode = hour < 6 || hour >= 18; // Dark mode from 6PM to 6AM
    setIsDarkMode(autoDarkMode);
    document.documentElement.setAttribute('data-theme', autoDarkMode ? 'dark' : 'light');
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    document.documentElement.setAttribute('data-theme', newMode ? 'dark' : 'light');
  };

  // Menu items using array
  const menuItems = [
    { id: 1, name: 'Home', link: '/' },
    { id: 2, name: 'Services', link: '/services' },
    { id: 3, name: 'Gallery', link: '/gallery' },
    { id: 4, name: 'About', link: '/about' },
    { id: 5, name: 'Contact', link: '/contact' },
  ];

  return (
    <div className="container">
      <Header menuItems={menuItems} isDarkMode={isDarkMode} toggleTheme={toggleTheme} />

      <section className="hero">
        <h2 className="section-title">Premium Pool Services</h2>
        <p>Transform your backyard into a luxurious oasis with our expert pool design and maintenance services.</p>
      </section>

      <h2 className="section-title">Our Services</h2>
      <div className="card-grid">
        <div className="card">
          <h3>Pool Design</h3>
          <p>Custom pool designs tailored to your space and lifestyle.</p>
        </div>
        <div className="card">
          <h3>Installation</h3>
          <p>Professional installation with premium materials.</p>
        </div>
        <div className="card">
          <h3>Maintenance</h3>
          <p>Regular cleaning and maintenance to keep your pool perfect.</p>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default App;
