import React, { useState } from 'react';
import '../styles/carousel.css'; // We'll create this next

// Import your pool images
import Pool1 from '../assets/images/Pool1.jpg';
import Pool2 from '../assets/images/Pool2.jpg';
import Pool3 from '../assets/images/Pool3.jpg';

const Carousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [
    { src: Pool1, alt: "Infinity edge pool with ocean view" },
    { src: Pool2, alt: "Modern rectangular pool design" },
    { src: Pool3, alt: "Family pool with luxury patio" }
  ];

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

    useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [images.length]);

  return (
    <div className="carousel">
      <div className="carousel-inner">
        {images.map((image, index) => (
          <div 
            className={`carousel-item ${index === currentIndex ? 'active' : ''}`}
            key={index}
          >
            <img 
              src={image.src} 
              alt={image.alt}
              className="carousel-image"
            />
            <div className="carousel-caption">
              <p>{image.alt}</p>
            </div>
          </div>
        ))}
      </div>
      <button className="carousel-control prev" onClick={prevSlide}>
        &lt;
      </button>
      <button className="carousel-control next" onClick={nextSlide}>
        &gt;
      </button>
      <div className="carousel-indicators">
        {images.map((_, index) => (
          <button
            key={index}
            className={`indicator ${index === currentIndex ? 'active' : ''}`}
            onClick={() => setCurrentIndex(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default Carousel;
